import logging as log
from bx.command import Command

class ListsCommand(Command):
    '''Manages experiment lists curated by BarcelonaBeta.

    Available subcommands:
     display:\t\tdisplay all existing lists (usage: bx lists display)'''
    nargs = 1
    subcommands = ['display']


    def __init__(self, *args, **kwargs):
        super(ListsCommand, self).__init__(*args, **kwargs)

    def parse(self):
        if self.args[0] == 'display':
            from bx import lists
            d = lists.__dict__

            keys = [e for e in list(d.keys()) if e.isupper()]
            keys.remove('DEV')
            keys = ['%s (%s images/sessions)'%(e, len(getattr(lists, e))) for e in keys]
            log.info('\nAvailable lists :\n\n - %s'%'\n - '.join(keys))
